    headers: {
      'content-type': 'application/json',
      'apikey': 'FcKdtJs202204',
      'username': "KimMyungSeong",
    },
    data: {
      "title":todosTitle,
      "order":todosOrderArray[todosIndex],
      "done" : !todosDoneArray[todosIndex]
    }
  })
  readTodo()
}

async function renderTodos(data) {
  
  const todos = await data.map((todo) => /* html */ `
  <li class="todo">
  <div class="todos--title">${todo.title}</div>
  <span>(${todo.createdAt.substr(2,2)}-${todo.createdAt.substr(5,2)}-${todo.createdAt.substr(8,2)} ${todo.createdAt.substr(11,2)}:${todo.createdAt.substr(14,2)}ë¶„ ìž‘ì„±)</span>
