import axios from "axios";
import regeneratorRuntime from "regenerator-runtime";
import './app.mjs'
import './main.css'


// DOM 요소 상단으로 올리기
const application = document.querySelector("#app-contents");
const loadingEl = document.querySelector("#loading");
const todoFormEl = document.querySelector("#todo-form");
const todosInputEl = document.querySelector("#todos-input");
const todosCountEl = document.querySelector(".todos-count");
const todosCountWrapper = document.querySelector(".todos-count-wrapper");
const todoList = document.querySelector(".todos--list");
const showDoneListButton = document.querySelector(
  "#todos--remote-show-donelist-button"
);
const showProgressingListButton = document.querySelector(
  "#todos--remote-show-progressinglist-button"
);

// 이벤트 바인딩 상단 배치 (
// 상단에 배치하는건 케바케 함수 표현식이면 호이스팅 문제가 있기 때문 그래도 한쪽에 몰아넣어서 알아보기 쉽게 구성
todoFormEl.addEventListener("submit", onSubmitTodo);
todosInputEl.addEventListener("focus", onFocusPlaceholder);
todosInputEl.addEventListener("blur", onBlurPlaceholder);
showDoneListButton.addEventListener("click", () => onToggleList(true));
showProgressingListButton.addEventListener("click", () => onToggleList(false));

// API 요청에 필요한 정보들은 상수로 선언해서 사용하기
const API_URL =
  "https://asia-northeast3-heropy-api.cloudfunctions.net/api/todos";
const API_KEY = "FcKdtJs202204";
const USER_NAME = "KimMyungSeong";

// 렌더링에 필요한 전역변수 todos, 그외 loading, todosValue 필요 없는 전역 변수 제거
let todos = [];

// 공통으로 사용할 수 있는 custom axios util 함수
// default 매개변수 지정
async function request({ url = API_URL, method = "", data = {} }) {
  const response = await axios({
    url,
    method,
    headers: {
      "content-type": "application/json",
      apikey: API_KEY,
      username: USER_NAME,
    },
    data,
  });
  return response;
}

// 페이지 최초 접근 시 실행하는 함수
async function onInit() {
  await getTodo();

  renderTodos();
  countTodos();
}

// axios.get 함수 분리
async function getTodo() {
  toggleLoading(true);

  try {
    const response = await request({
      method: "GET",
    });

    // todos 초기화 후 데이터 입력
    todos = [];
    response.data.forEach((item) => todos.push(item));

    return response;
  } catch (err) {
    alert(err);
  } finally {
    // finally에서 toggleLoading 실행
    toggleLoading(false);
  }
}

// axios.post 함수 분리
async function postTodo(todosValue, orderNumber) {
  try {
    const response = await request({
      method: "POST",
      data: {
        title: todosValue,
        order: orderNumber,
      },
    });
    // 렌더링에 이용되는 todos에다 push
    todos.push(response.data);
    readTodo();

    return response;
  } catch (err) {
    alert(err);
  }
}

// axios.delete 분리
async function deleteTodo(e) {
  // button에 value로 각 아이템의 id를 넣었기 때문에 이를 이용해 delete
  const { value } = e.target;

  try {
    const response = await request({
      url: `${API_URL}/${value}`,
      method: "DELETE",
    });

    // 렌더링에 이용되는 전역 변수 todos에서 filter를 이용해 제거
    todos = todos.filter((item) => item.id !== value);
    readTodo();

    return response;
  } catch (err) {
    alert(err);
  }
}

// axios.put 분리
async function putTodo(item) {
  const { title, order, done, id } = item;

  try {
    const response = await request({
      url: `${API_URL}/${id}`,
      method: "PUT",
      data: { title, order, done: !done },
    });

    // todos에서 현재 선택 todo index 가져옴
    const currentIdx = todos.findIndex((item) => item.id === id);

    // todos 기존 데이터 변경
    todos[currentIdx] = {
      ...todos[currentIdx],
      title,
      order,
      done: !done,
    };
    readTodo();

    return response;
  } catch (err) {
    alert(err);
  }
}

// validate
function sameTodoValidation(array, value) {
  // some을 통한 중복된 값 찾기
  const isValid = array.some((item) => value === item.title);
  return isValid;
}

// Create Todo
async function createTodo(todosValue) {
  if (!sameTodoValidation(todos, todosValue)) {
    // orderNumber 전역 변수 제거
    postTodo(todosValue, todos.length + 1);
  } else {
    alert("동일한 값");
  }
}

// Submit Todo
function onSubmitTodo(e) {
  e.preventDefault();

  if (todosInputEl.value.length < 10) {
    alert("누가 목표는 자세히 써야 실천 한다고 하더라고요(10자 이상 입력)");
    return;
  }
  createTodo(todosInputEl.value);

  todosInputEl.value = "";
  todosInputEl.focus();
}

function countTodoListChildNode() {
  // append보다 innerHTML이 좀 더 깔끔띠 합니다?
  if (!todos.length) {
    todoList.innerHTML = `
      <div>아무 계획도 없습니다...</div>
    `;
  }
}

// Change Done Property
async function onToggleDone(e) {
  const { value } = e.target;
  const currentItem = todos.find((item) => item.id === value);

  putTodo(currentItem);
}

// readTodo 매번 axios.get하기에는 리소스 부담이 커서 전역 변수 todos로 화면 그려주는 역할
function readTodo() {
  renderTodos();
  countTodos();
}

/**
 *
 *
 * 2022 / 05 / 19 여기까지 코드 리뷰 수정
 *
 *
 */

//PUT change comment
let showUpdateInput = false;

function onToggleUpdateInput(elems, bool = true) {
  console.log("toggle!");
  showUpdateInput = bool;
  if (showUpdateInput) {
    elems.style.display = "";
    elems.style.display = "block";
  } else if (!showUpdateInput) {
    elems.style.display = "";
    elems.style.display = "none";
  }
}

async function changeTodoTitle(e) {
  const { data } = await axios({
    url: "https://asia-northeast3-heropy-api.cloudfunctions.net/api/todos",
    method: "GET",
    headers: {
      "content-type": "application/json",
      apikey: API_KEY,
      username: USER_NAME,
    },
  });
  let todosTitleArray = [];
  data.map((item) => todosTitleArray.push(item.title));
  const todosTitle =
    e.target.parentNode.parentNode.parentNode.firstChild.nextSibling
      .textContent;
  const todosIndex = todosTitleArray.findIndex((title) => title === todosTitle);
  const todoCard = document.querySelectorAll(".todo")[todosIndex];
  const updateInputBox = document.querySelector(".todos--update-input-box");
  const todosUpdateCancelButton = document.querySelector(
    ".todos--update-cancel-button"
  );
  todosUpdateCancelButton.addEventListener("click", () => {
    onToggleUpdateInput(updateInputBox, false);
  });
  document.body.append(updateInputBox);
  onToggleUpdateInput(updateInputBox);

  let todosIdArray = [];
  data.map((item) => todosIdArray.push(item.id));

  let todosOrderArray = [];
  data.map((item) => todosOrderArray.push(item.order));

  let todosDoneArray = [];
  data.map((item) => todosDoneArray.push(item.done));
  console.log(todosDoneArray[todosIndex]);

  readTodo();
}

// 들여쓰기로 태그 인지할 수 있게 수정 및 button에다 value로 todo.id 넣기
async function renderTodos(str = "작성") {
  console.log(todos);
  const todoElements = await todos.map(
    (todo) => `
    <li class="todo">
      <div class="todos--title">${todo.title}</div>
      <span>
        (${todo.updatedAt.substr(2, 2)}-${todo.updatedAt.substr(5, 2)}
          - ${todo.updatedAt.substr(8, 2)} ${todo.updatedAt.substr(11, 2)}
          : ${todo.updatedAt.substr(14, 2)}분 ${str})
      </span>
      <div class="todos--button-wrapper">
        <div>
          <button class="todos--delete-button" value=${
            todo.id
          }>삭제하기</button>
          <button class="todos--update-button" value=${
            todo.id
          }>타협하기</button>
        </div>
      </div>
      <div>${todo.done === false ? "노력 중🔴" : "해냈어요!🔵"}
        <button class='todos--done-toggle-button' value=${todo.id}>체크</button>
      </div>
    </li>
  `
  );
  const todoTitles = todoElements.join("");
  todoList.innerHTML = todoTitles;
  application.append(todoList);
  const doneToggleButtons = document.querySelectorAll(
    ".todos--done-toggle-button"
  );
  doneToggleButtons.forEach((doneToggleButton) =>
    doneToggleButton.addEventListener("click", onToggleDone)
  );
  countTodoListChildNode();
  loadButtons();
}

const showAllListButton = document.querySelector(
  "#todos--remote-show-Alllist-button"
);
function showAllList(e) {
  readTodo();
}
const deleteDoneListButton = document.querySelector(
  "#todos--remote-remove-donelist-button"
);

async function deleteDoneList(e) {
  const { data } = await axios({
    url: API_URL,
    method: "GET",
    headers: {
      "content-type": "application/json",
      apikey: API_KEY,
      username: USER_NAME,
    },
  });
  const doneTodo = data.filter((data) => data.done === true);
  const todosIdArray = [];
  doneTodo.map((item) => todosIdArray.push(item.id));
  todosIdArray.forEach((todoId) =>
    axios({
      url: `https://asia-northeast3-heropy-api.cloudfunctions.net/api/todos/${todoId}`,
      method: "DELETE",
      headers: {
        "content-type": "application/json",
        apikey: API_KEY,
        username: USER_NAME,
      },
    })
  );
  readTodo();
}
function loadButtons() {
  const deleteButtonEls = document.querySelectorAll(".todos--delete-button");
  const todosHandleEls = [
    ...document.querySelectorAll(".todos--button-wrapper"),
  ];
  const updateButtonEls = [
    ...document.querySelectorAll(".todos--update-button"),
  ];
  showAllListButton.addEventListener("click", showAllList);
  deleteDoneListButton.addEventListener("click", deleteDoneList);
  deleteButtonEls.forEach((deleteButtonEl) =>
    deleteButtonEl.addEventListener("click", deleteTodo)
  );
  updateButtonEls.forEach((updateButtonEl) =>
    updateButtonEl.addEventListener("click", changeTodoTitle)
  );
}

//sort by data.done value
async function onToggleList(bool) {
  const { data } = await axios({
    url: "https://asia-northeast3-heropy-api.cloudfunctions.net/api/todos",
    method: "GET",
    headers: {
      "content-type": "application/json",
      apikey: API_KEY,
      username: "KimMyungSeong",
    },
  });
  const filteredFalseData = data.filter((item) => item.done === bool);
  console.log(filteredFalseData);
  todoList.innerHTML = "";
  renderTodos(filteredFalseData, "수정");
}

